clinical_table <- clinical_table %>%
  tab_style(
    style = list(
      cell_text(weight = "bold"),
      cell_fill(color = "#E8F4F8")
    ),
    locations = cells_body(
      columns = everything(),
      rows = p_value < 0.05
    )
  ) %>%
  tab_style(
    style = cell_text(weight = "bold"),
    locations = cells_column_labels(everything())
  )

